        }

        // Take user sign-in information
        public static void TakeSignIn()
        {
            try
            {
                Console.Clear(); // Clear the console
                ConsoleUtility.Header(); // Display the header
                Console.ForegroundColor = ConsoleColor.Green; // Set text color

                // Prompt user for input
                Console.WriteLine("Enter Your Name: ");
                name = Console.ReadLine(); // Read user's name
                Console.WriteLine("Enter Your PakNo");
                PakNo = int.Parse(Console.ReadLine()); // Read user's PakNo
                Console.WriteLine("Enter Your Password: ");
                Password = Console.ReadLine(); // Read user's password
            }
            catch (Exception ex)
            {
                Console.WriteLine($"{ex.Message}"); // Print error message
            }
        }

        // Display database choice menu
        public static int DataBaseChoice()
        {
            int op;
            try
            {
                Console.Clear(); // Clear the console
                Header(); // Display the header

                // Display database options
                Console.WriteLine("WHICH DATA SERVER DO YOU WANT TO USE?");
                Console.WriteLine("1. DataBase");
                Console.WriteLine("2. File Handling");
                Console.WriteLine("Enter your choice: ");
                op = int.Parse(Console.ReadLine()); // Read user's choice

                if (op == 1 || op == 2)
                {
                    return op; // Return valid choice
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex.ToString()); // Print error message
            }
            return -1; // Return invalid choice
        }

        // Display error for invalid option
        public static void Error()
        {
            Console.WriteLine("Invalid Option");
        }

        // Display error for user not found
        public static void UserError()
        {
            Console.WriteLine("User not Found");
        }
